import java.util.ArrayList;
import java.util.List;
import javax.persistence.Basic;
import javax.persistence.CascadeType;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.GeneratedValue;
import javax.persistence.Id;
import javax.persistence.OneToMany;
import javax.persistence.Version;

/**
 * <ul>
 * <li>create table Mailbox (id bigint generated by default as identity (start with 1), desc varchar(255), title varchar(255), version integer not null, primary key (id))
 * <li>create table Message (uid bigint not null, content varchar(255), fromAddr varchar(255), title varchar(255), toAddrs varchar(255), version integer not null, mailbox_id bigint, primary key (mailbox_id, uid))
 * <li>alter table Message add constraint FK_d9ixynjnohap20r8qwkf2x99u foreign key (mailbox_id) references Mailbox
 * </ul>
 * @author Github jjYBdx4IL Projects
 */
@Entity
public class Mailbox {

    public Mailbox() {
        messages = new ArrayList<>();
    }

    @Id
    @GeneratedValue
    private Long id;
    @Basic
    private String title;
    @Basic
    private String desc;
    @OneToMany(fetch = FetchType.LAZY, cascade = CascadeType.ALL, mappedBy="mailbox")
    public List<Message> messages;
    @Version
    private int version;

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    /**
     * @return the title
     */
    public String getTitle() {
        return title;
    }

    /**
     * @param title the title to set
     */
    public void setTitle(String title) {
        this.title = title;
    }

    /**
     * @return the desc
     */
    public String getDesc() {
        return desc;
    }

    /**
     * @param desc the desc to set
     */
    public void setDesc(String desc) {
        this.desc = desc;
    }

    @Override
    public String toString() {
        StringBuilder builder = new StringBuilder();
        builder.append("Mailbox [");
        builder.append("desc=");
        builder.append(desc);
        builder.append(", id=");
        builder.append(id);
        builder.append(", messages=");
        builder.append(messages);
        builder.append(", title=");
        builder.append(title);
        builder.append(", version=");
        builder.append(version);
        builder.append("]");
        return builder.toString();
    }

}
