import java.util.ArrayList;
import java.util.List;
import javax.persistence.CascadeType;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.GeneratedValue;
import javax.persistence.Id;
import javax.persistence.OneToMany;
import javax.persistence.Version;

/**
 * <ul>
 * <li>create table Post (id bigint generated by default as identity (start with 1), content varchar(255),
 * title varchar(255), version integer not null, user_id bigint, primary key (id))
 * <li>create table UserFKJoin (id bigint generated by default as identity (start with 1), version integer not null,
 primary key (id))
 <li>alter table Post add constraint FK_s7ui38nhrh0c7q4nfpgf93bfk foreign key (user_id) references UserFKJoin
 </ul>
 *
 * @author Github jjYBdx4IL Projects
 */
@Entity
public class UserFKJoin {

    @Id
    @GeneratedValue
    private Long id;
    @OneToMany(cascade = CascadeType.ALL, fetch = FetchType.LAZY, mappedBy = "user")
    public List<Post> posts;
    @Version
    private int version;

    public UserFKJoin() {
        posts = new ArrayList<>();
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    @Override
    public String toString() {
        StringBuilder builder = new StringBuilder();
        builder.append("User [");
        builder.append("id=");
        builder.append(id);
        builder.append(", posts=");
        builder.append(posts);
        builder.append(", version=");
        builder.append(version);
        builder.append("]");
        return builder.toString();
    }

}
