import java.util.ArrayList;
import java.util.List;
import javax.persistence.CascadeType;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.GeneratedValue;
import javax.persistence.Id;
import javax.persistence.OneToMany;
import javax.persistence.Version;

/**
 * <ul>
 * <li>create table PostNoMappedBy (id bigint generated by default as identity (start with 1), content varchar(255), title varchar(255), version integer not null, user_id bigint not null, primary key (id))
 * <li>create table UserNoMappedBy (id bigint generated by default as identity (start with 1), version integer not null, primary key (id))
 * <li>create table UserNoMappedBy_PostNoMappedBy (UserNoMappedBy_id bigint not null, posts_id bigint not null)
 * <li>alter table UserNoMappedBy_PostNoMappedBy drop constraint UK_2axuq857yk5sr04j95i0y2mw
 * <li>alter table UserNoMappedBy_PostNoMappedBy add constraint UK_2axuq857yk5sr04j95i0y2mw  unique (posts_id)
 * <li>alter table PostNoMappedBy add constraint FK_fhjgo73dfrt05efm6ytk5jib0 foreign key (user_id) references UserNoMappedBy
 * <li>alter table UserNoMappedBy_PostNoMappedBy add constraint FK_2axuq857yk5sr04j95i0y2mw foreign key (posts_id) references PostNoMappedBy
 * <li>alter table UserNoMappedBy_PostNoMappedBy add constraint FK_6afj2259vs41j4x08yi2jvi7t foreign key (UserNoMappedBy_id) references UserNoMappedBy
 * </ul>
 *
 * @author Github jjYBdx4IL Projects
 */
@Entity
public class UserNoMappedBy {

    @Id
    @GeneratedValue
    private Long id;
    @OneToMany(cascade = CascadeType.ALL, fetch = FetchType.LAZY)
    public List<PostNoMappedBy> posts;
    @Version
    private int version;

    public UserNoMappedBy() {
        posts = new ArrayList<>();
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    @Override
    public String toString() {
        StringBuilder builder = new StringBuilder();
        builder.append("User [");
        builder.append("id=");
        builder.append(id);
        builder.append(", posts=");
        builder.append(posts);
        builder.append(", version=");
        builder.append(version);
        builder.append("]");
        return builder.toString();
    }

}
